(function () {

    // Setting : For setting the upper limit of image memory cache. (MPix)
    var cacheLimit = 10;

    // Setting : For setting the screen width.
    var screenWidth = 816;

    // Setting : For setting the screen height.
    var screenHeight = 624;

    // Setting : Rendering mode (canvas/webgl/auto)
    var renderingMode = "auto";

    // Setting : To set initial value as to whether the player always dashes. (on/off)
    var alwaysDash = "off";

    // Setting : If set, change window width to this value
    var windowWidthTo = 0;

    // Setting : If set, change window height to this value
    var windowHeightTo = 0;

    var windowWidth;
    var windowHeight;

    if (windowWidthTo) {
        windowWidth = windowWidthTo;
    } else if (screenWidth !== SceneManager._screenWidth) {
        windowWidth = screenWidth;
    }

    if (windowHeightTo) {
        windowHeight = windowHeightTo;
    } else if (screenHeight !== SceneManager._screenHeight) {
        windowHeight = screenHeight;
    }


    ImageCache.limit = cacheLimit * 1000 * 1000;
    SceneManager._screenWidth = screenWidth;
    SceneManager._screenHeight = screenHeight;
    SceneManager._boxWidth = screenWidth;
    SceneManager._boxHeight = screenHeight;

    SceneManager.preferableRendererType = function () {
        if (Utils.isOptionValid('canvas')) {
            return 'canvas';
        } else if (Utils.isOptionValid('webgl')) {
            return 'webgl';
        } else if (renderingMode === 'canvas') {
            return 'canvas';
        } else if (renderingMode === 'webgl') {
            return 'webgl';
        } else {
            return 'auto';
        }
    };

    var _ConfigManager_applyData = ConfigManager.applyData;
    ConfigManager.applyData = function (config) {
        _ConfigManager_applyData.apply(this, arguments);
        if (config['alwaysDash'] === undefined) {
            this.alwaysDash = alwaysDash;
        }
    };


    var _SceneManager_initNwjs = SceneManager.initNwjs;
    SceneManager.initNwjs = function () {
        _SceneManager_initNwjs.apply(this, arguments);

        if (Utils.isNwjs() && windowWidth && windowHeight) {
            var dw = windowWidth - window.innerWidth;
            var dh = windowHeight - window.innerHeight;
            window.moveBy(-dw / 2, -dh / 2);
            window.resizeBy(dw, dh);
        }
    };
})();
